  msg.payload.thingsboard.enabled = msg.payload.thingsboard.enabled === true ? true : false;
} else { msg.payload.thingsboard.enabled = false; }

if (msg.payload.thingsboard.enabled === true) {
  if (typeof msg.payload.thingsboard.host === "undefined") {
    node.error(`Node-RED config.yml key thingsboard.host needs to be populated when thingsboard is enabled. Disabling ThingsBoard.`);
    // and disable thingsboard
    msg.payload.thingsboard.enabled = false;
  }
  else {
    //assign TB url (domain, front part)
    msg.payload.thingsboard.url = `${msg.payload.thingsboard.host}${msg.payload.thingsboard.port ? ':' + msg.payload.thingsboard.port : ''}`
  }
}


//deployment
//check deployment entries for valid values
msg.payload.deployment.lat = typeof msg.payload.deployment.lat === "undefined" ? "" : msg.payload.deployment.lat;
msg.payload.deployment.lon = typeof msg.payload.deployment.lon === "undefined" ? "" : msg.payload.deployment.lon;
msg.payload.deployment.bearing = typeof msg.payload.deployment.bearing === "undefined" ? "" : msg.payload.deployment.bearing;

//check for numeric (decimal) lon and lat
if (typeof msg.payload.deployment.lat !== "undefined") {
  if (typeof msg.payload.deployment.lat !== "number") {
    node.error(`Node-RED config.yml top-level deployment.lat is not a valid type; needs to be numeric`);
  }
}
if (typeof msg.payload.deployment.lon !== "undefined") {
  if (typeof msg.payload.deployment.lon !== "number") {
    node.error(`Node-RED config.yml top-level deployment.lon is not a valid type; needs to be numeric`);
  }
}
const bearing_values = ['n', 's', 'e', 'w', 'ne', 'nw', 'se', 'sw'];
//allow empty/null/undefined values
if (typeof msg.payload.deployment.bearing !== "undefined") {
  if (bearing_values.indexOf(msg.payload.deployment.bearing.toLowerCase()) !== -1) {
    msg.payload.deployment.bearing = msg.payload.deployment.bearing.toLowerCase();
  }
  else {
    node.error(`Node-RED config.yml top-level deployment.bearing is not a valid value; needs to be one of ${bearing_values}`);
  }
}

// go through each of the sensors that have no deployment object and copy the top-level deployment object into them
// do not overwrite any existing sensor deployment properties
for (const [sensorType, sensors] of Object.entries(msg.payload.sensors)) {
  for (const [sensorName, sensor] of Object.entries(sensors)) {
    if (sensor.hasOwnProperty('enabled') && sensor.enabled === true) {
      if (sensor.hasOwnProperty('deployment')) {
        const sensorDeployment = sensor.deployment;
        const topLevelDeployment = msg.payload.deployment;
        for (const [prop, value] of Object.entries(topLevelDeployment)) {
          if (!sensorDeployment.hasOwnProperty(prop)) {
            sensorDeployment[prop] = value;
          }
        }
        if (sensorDeployment.hasOwnProperty('bearing')) {
          const bearing = sensorDeployment.bearing.toLowerCase();
          if (!bearing_values.includes(bearing)) {
            node.error(`Invalid bearing value '${sensorDeployment.bearing}' for sensor '${sensorType}.${sensorName}'. Using default value ''.`, msg);
            sensorDeployment.bearing = '';
          }
        }
      } else {
        msg.payload.sensors[sensorType][sensorName].deployment = { ...msg.payload.deployment };
      }
    }
  }
}

//time
msg.payload.time.npt_set === false ? false : true;

return msg;

// msg.sqlActiveDeployments (sql query results, array of objects)
// msg.sensors (sensorType.sensorName.properties)

let newArr = {};
newArr.updatedDeployment = [];
newArr.currentDeployment = [];

const sqlresults = msg.sqlActiveDeployments;

//go through each sensor, see if new deployment configs (lat, lon, bearing) are different from previous deployment
for (const [sensorType, sensors] of Object.entries(msg.sensors)) {
    for (const [sensorName, sensor] of Object.entries(sensors)) {
        if (sensor.hasOwnProperty('enabled') && sensor.enabled === true) {
            if (sensor.hasOwnProperty('deployment')) {
                const sensorDeployment = sensor.deployment;

                let newMsg = {};
                newMsg.payload = {};
                let updateMsg = {};
                updateMsg.payload = {};
                let mergedMsg = {};

                let hasDifference = false;
                newMsg.sqlSend = false;

                //find matching sqlresult(s)
                const sqlMatchObj = sqlresults.find(o => o.sensorName === sensorName);

                if (sqlMatchObj) {
                    //fields to check for differences
                    ["lat", "lon", "bearing"].forEach(property => {
                        if (sqlMatchObj[property] !== sensorDeployment[property]) {
                            hasDifference = true;
                        }
                    });
                }

                //if new deployment, UPDATE old record's entryEndDateTime 
                if (hasDifference) {
                    updateMsg.payload.id = sqlMatchObj.id;
                    updateMsg.payload.entryEndDateTime = (new Date()).getTime();

                    //send update query, updateMsg
                    updateMsg.topic = `UPDATE deployment SET entryEndDateTime = ${updateMsg.payload.entryEndDateTime} WHERE id = '${updateMsg.payload.id}'`;
                    updateMsg.sqlSend = true;
                    node.send(updateMsg);
                    mergedMsg = {...updateMsg.payload, ...sqlMatchObj};
                    newArr.updatedDeployment.push(mergedMsg);
                }

                //if new deployment from difference or non-existing, and add new record
                if (hasDifference || !sqlMatchObj) {
                    const newDateTime = new Date();
                    newMsg.payload.id = newDateTime.getTime() + '-' + Math.floor(Math.random() * 0xFFFFFFFF).toString(16).padStart(8, '0') + '-d';
                    newMsg.payload.entryStartDateTime = newDateTime.getTime();
                    newMsg.payload.entryEndDateTime = '';
                    newMsg.payload.lat = sensorDeployment["lat"];
                    newMsg.payload.lon = sensorDeployment["lon"];
                    newMsg.payload.bearing = sensorDeployment["bearing"];
                    newMsg.payload.sensorName = sensorName;
                    newMsg.payload.deviceName = global.get("deviceAttributes.deviceName", 'config');
                    //send add record query, newPayload
                    newMsg.topic = `INSERT INTO deployment (id, entryStartDateTime, lat, lon, bearing, sensorName, deviceName) VALUES ('${newMsg.payload.id}', ${newMsg.payload.entryStartDateTime}, ${newMsg.payload.lat}, ${newMsg.payload.lon}, '${newMsg.payload.bearing}', '${newMsg.payload.sensorName}', '${newMsg.payload.deviceName}')`;
                    newMsg.sqlSend = true;
                    node.send(newMsg);

                    //update config with id
                    global.set(`config.sensors.${sensorType}.${sensorName}.deployment.id`, newMsg.payload.id, 'config');
                }
                else {
                    newMsg.payload = sqlMatchObj;
                }
                //collect all sensor info for output
                newArr.currentDeployment.push(newMsg.payload);
            }
        }
    }
}

msg = {};
