}, {});

// Calculate the median velocity for each direction
for (const direction in groupedData) {
  if (groupedData.hasOwnProperty(direction)) {
    const sortedVelocities = groupedData[direction].map(entry => entry.velocity).sort((a, b) => a - b);
    const medianIndex = Math.floor(sortedVelocities.length / 2);
    const medianVelocity =
      sortedVelocities.length % 2 === 0
        ? (sortedVelocities[medianIndex - 1] + sortedVelocities[medianIndex]) / 2
        : sortedVelocities[medianIndex];
    medianVelocityPerDirection[direction] = medianVelocity;
  }
}

